import React, { useState, useEffect } from "react";
import '../styles/Header.css';
import cartIcon from '../images/cart-icon-2.png';
import { Link } from "react-router-dom";

function Header() {

  const [itemsInCart, setItemsInCart] = useState(0);
  const [activeLink, setActiveLink] = useState('/');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset;
      if (scrollTop > 0) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className={`header ${scrolled ? 'scrolled' : ''}`}>
      <div className="header-title-section">
        <div className="header-title">ARCHIVE</div>
        <div className="header-links">
        <div className="header-link" onClick={() => setActiveLink('/')}>
  <Link to={'/'} className={activeLink === '/' ? 'active' : ''}>Home</Link>
</div>
<div className="header-link" onClick={() => setActiveLink('/mens')}>
  <Link to={'/mens'} className={activeLink === '/mens' ? 'active' : ''}>Men</Link>
</div>
<div className="header-link" onClick={() => setActiveLink('/womens')}>
  <Link to={'/womens'} className={activeLink === '/womens' ? 'active' : ''}>Women</Link>
</div>
<div className="header-link" onClick={() => setActiveLink('/about')}>
  <Link to={'/about'} className={activeLink === '/about' ? 'active' : ''}>About</Link>
</div>
        </div>
      </div>
      <div className="cart"><img src={cartIcon} alt='cart-icon'/></div>
      <div className="items-in-cart">{itemsInCart}</div>
    </div>
  )
}

export default Header;
