import React, { useState } from 'react';
import '../styles/Mens.css';
import cards from '../components/cards';
import Item from '../components/Item';

function Mens() {
  const [selectedItem, setSelectedItem] = useState(null);

  const menCards = cards.filter((card) => card.gender === 'men');

  // show item details when a card is clicked
  const handleCardClick = (card) => {
    setSelectedItem(card);
  };

  // hide item details when the close button is clicked
  const handleCloseButtonClick = () => {
    setSelectedItem(null);
  };

  return (
    <div className='mens'>
      <div className='mens-image-section'></div>
      <div className='mens-products'>
        {menCards.map((card) => (
          <div key={card.title} className='card-container'>
            <div
              className='card'
              style={{ backgroundImage: `url(${card.src})` }}
              onClick={() => handleCardClick(card)}
            >
              <div className='overlay'>
                <h3 className='title'>{card.title}</h3>
                <p className='brand'>{card.brand}</p>
                <p className='description'>{card.description}</p>
              </div>
              <p className='price'>${card.price}</p>
            </div>
          </div>
        ))}
      </div>
      {selectedItem && ( // render Item component when an item is selected
        <div className='item-modal'>
          <div className='item-modal-content'>
            <Item item={selectedItem} />
            <button
              className='item-close-button'
              onClick={handleCloseButtonClick}
            >
              X
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Mens;
