import { useContext } from "react";
import '../styles/Cart.css';
import CartContext from "../context/cart/CartContext";
import CartItem from "./CartItem";

const Cart = () => {
  const { showCart, cartItems, showHideCart } = useContext(CartContext);

  return (
    <>
      {showCart && (
        <div className="cart-wrapper">
          <div className="cart-header">
            <button className="close-cart-button" onClick={showHideCart}>&#10005;</button>
          </div>
          <div style={{textAlign: 'right'}}></div>
          <div className="cart-inner-wrapper">
            {cartItems.length === 0 ? (<h4>cart is empty</h4>) : (
              <div>
                {cartItems.map(item => (
                  <CartItem key={item.id} item={item} src={item.src} title={item.title} />
                ))}
              </div>
            )}
          </div>
          <div className="cart-total">
            <div>Cart Total: </div>
            <div>${cartItems.reduce((amount, item) => item.price + amount, 0)}</div>
          </div>
        </div>
      )}
    </>
  )
}

export default Cart;
